import { PEEL_THE_POTATO, DO_SOMETHING } from "potatoActions.js";
import requester from "../../utils/requester.js";

// SUBSCRIBE TO STATE???
const state = {} // STORE.potato ????

const potatoInitalState = {};

export function potatoReducer(state, action) {
  switch (action.type) {
    case PEEL_THE_POTATO:
      return Object.assign({}, state, action.payload)  // <–– return new state
    case DO_SOMETHING:
      return Object.assign({}, state, action.payload)
    case REQ_TO_DATABASE:
    	requester.editPotato(action.payload.id, action.payload.WHATEVER);
      return Object.assign({}, state, action.payload)
    default:
      return state;
  }
}

// TESING
export function onSuccessFunction(res) {
	console.log("onSuccessFunction Res:", res);
}
