// import "./potatoActions.js";
import requester from "../../utils/requester.js";

export const initialState = {
	skinColor: "red",
}

function potatoReducer(state = [], action) {
  switch (action.type) {
    case "PEEL_THE_POTATO":
    	console.log("we made it to the potatoReducer");
      return Object.assign({}, state, action.payload)  // <–– return new state
    case "DO_SOMETHING":
      return Object.assign({}, state, action.payload)
    case "REQ_TO_DATABASE":
    	requester.editPotato(action.payload.id, action.payload.WHATEVER);
      return Object.assign({}, state, action.payload)
    default:
      return state;
  }
}

export default potatoReducer;
